import { useEffect, useState } from "react";

function App() {
  const [loading, setLoading] = useState(true);
  const [coins, setCoins] = useState([]); //기본 값을 빈칸 말고 비어있는 array라도 주기, 컴포넌트의 시작인 coins가 undefinded면 length가 없어 오류나기 때문
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
      .then((response) => response.json())
      .then((json) => {
        //코인 data를 json에 가지고 있음
        setCoins(json); //data를 우리의 컴포넌트에서 보여주는 법? data를 State에 넣기!!!
        setLoading(false);
      });
  }, []); //아무것도 주시x = 코드 실행 최초 1회만
  return (
    <div>
      <h1>The Coins! ({coins.length})</h1>
      {loading ? (
        <strong>Loading...</strong>
      ) : (
        <select>
          {coins.map((coin) => (
            <option>
              {coin.name} ({coin.symbol}) : ${coin.quotes.USD.price}
            </option>
          ))}
        </select>
      )}
    </div>
  );
}

export default App;
